mapper.select:
select a.*, (select b.name from sys_area b where b.id = a.parent_id) parentName, (SELECT c.name FROM sys_organ c WHERE c.[id]=a.organ_id) organName, (SELECT c.item_name FROM sys_dict c WHERE c.class_code='1006001' AND c.item_value=a.type) typeName, (SELECT c.item_name FROM sys_dict c WHERE c.class_code='1006002' AND c.item_value=a.provinces_type) typeName from sys_area a WHERE 1=1
and a.id = #{area.id}
and (a.name like '%'+#{area.name}+'%' or a.simple_wubi like '%'+#{area.name}+'%' or a.simple_spelling like '%'+#{area.name}+'%')
