mapper.select:
select u.* from sys_dict u WHERE 1=1 and u.class_code = #{param.classCode}
and (u.item_name like '%'+#{param.searchText}+'%' or u.simple_spelling like '%'+#{param.searchText}+'%' or u.simple_wubi like '%'+#{param.searchText}+'%')
order by u.sort

select count(1)
from sys_dict a
WHERE 1=1


        and (a.item_name = #{dict.itemName} or a.item_value = #{dict.itemValue})


        and a.item_name = #{dict.itemName}


        and a.item_value = #{dict.itemValue}



    and a.class_code = #{dict.classCode}


    and a.id != #{dict.id}

select count(1)
from sys_dict_class a
WHERE 1=1


        and (a.name = #{dictClass.name} or a.code = #{dictClass.code})


        and a.name = #{dictClass.name}


        and a.code = #{dictClass.code}



    and a.id != #{dictClass.id}

select a.*
from sys_dict_class a
WHERE a.parent_id = #{parentId}
