mapper.select:
select u.*, (select d.name from sys_organ d where d.id = u.organ_id) organName from sys_user u WHERE 1=1
and u.name like '%'+#{user.name}+'%'
and u.organ_id = #{user.organId}

select u.*, (select d.name from sys_organ d where d.id = u.organ_id) organName
from sys_user u
WHERE u.name = #{name}
